import { Chart, Line } from 'react-chartjs-2'

import * as zoom from 'chartjs-plugin-zoom'

import moment from 'moment'
import React from 'react'

class OrderbookGraph extends React.Component {
    render() {
        const data = {
            labels: this.props.data.map((d) => d.time),
			datasets: [
				{
					label: 'Bid', 
                    data: this.props.data.map((d) => d.bid),
					steppedLine: 'before',
					borderColor: "#00ff00"
                },
                {
					label: 'Ask', 
                    data: this.props.data.map((d) => d.ask),
					steppedLine: 'before',
					borderColor: "#ff0000"
				}
			]
        }

        const options = {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
                yAxes: [{
                    distribution: 'linear', 
                    ticks: { beginAtZero: true}
                }],
                xAxes: [{
                    type: 'time',
                    distribution: 'linear',
                    ticks: { source: 'data'}
                }]
            },
            pan: {
                enabled: true,
                mode: 'x',
                speed: 10,
                threshold: 10
            },
            zoom: {
                enabled: true, 
                mode: 'x',
                sensitivity: 0.25
            }
		}

 		console.log(data)
		return (<div className="orderbookGraph">
			<Line width={200} height={200} data={data} options={options}
            />
            </div>
		)
	}
}

export default OrderbookGraph
