import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Square extends React.Component {
    render() {
    const ask_levels = this.props.orderbook.map(
        (level) => {
            return <tr><td></td><td>{level.price}</td></tr>
        }
    )

    const bid_levels = this.props.orderbook.map(
        (level) => {
            return <tr><td>{level.price}</td><td></td></tr>
        }
    )

    return (
      <div>
      <table>
        <tr>
        <td className="bidHeader">Bid</td>
        <td className="askHeader">Ask</td>
        </tr>
        {ask_levels}

      </table>
      <button className="square" onClick={() => {this.props.onClick(); console.log("OnClick square")}}>
        {this.props.value}
      </button>
      </div>
    );
  }
}

class Board extends React.Component {
  constructor(props){
      super(props)
      this.state = {
          squares: Array(9).fill(null)
      }
  }

  renderSquare(i) {
      return <Square 
        orderbook={[{price: 10, volume: 11}, {price:12, volume:15}]}
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)} />
  }

  handleClick(i)
  {
      const squares = this.state.squares.slice();
      squares[i] = 'X';
      this.setState({squares: squares});
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
