import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class TradeInput extends React.Component {
    render() {
        return (
                <div className="tradeInput">
                <b>Price</b>
                <input type="text"></input>
                <b>Volume</b>
                <input type="text"></input>
                <div className="tradeInputButtons">
                <button className="buyButton">Buy</button>
                <button className="sellButton">Sell</button>
                </div>
                </div>
               );
    }
}


class OrderList extends React.Component {
    render() {
        const orders = this.props.orders.map(
                (order) => {
                return <tr>
                <td>{orders.side}</td><td>{orders.price}</td><td>{orders.volume}</td>
                <td><button className="cancelButton">X</button></td></tr>
                }
                )

            return (
                    <div className="orderList">
                    <table>
                    <tr><td><b>Side</b></td><td><b>Price</b></td><td><b>Volume</b></td><td></td></tr>
                    {orders}
                    </table>
                    </div>
                   );
    }
}

class TradeList extends React.Component {
    render() {
        const trades= this.props.trades.map(
                (trade) => {
                return <tr>
                <td>{trade.side}</td><td>{trade.price}</td><td>{trade.volume}</td></tr>
                }
                )

            return (
                    <div className="tradeList">
                    <table>
                    <tr><td><b>Side</b></td><td><b>Price</b></td><td><b>Volume</b></td></tr>
                    {trades}
                    </table>
                    </div>
                   );
    }
}

class Orderbook extends React.Component {
    render() {
        const ask_levels = this.props.orderbook.ask_levels.map(
                (level) => {
                return <tr>
                <td><button className="buyButton">Buy</button></td>
                <td className="bold">${level.price}</td><td>{level.volume}</td></tr>
                }
                )

            const bid_levels = this.props.orderbook.bid_levels.map(
                    (level) => {
                    return <tr><td>{level.volume}</td><td className="bold">${level.price}</td>
                    <td><button className="sellButton">Sell</button></td></tr>
                    }
                    )

            return (
                    <div>
                    <table>
                    <tr>
                    <td className="bidHeader">Bid</td><td className="priceHeader">Price</td>
                    <td className="askHeader">Ask</td>
                    </tr>
                    {ask_levels}
                    {bid_levels}
                    </table>
                    </div>
                   );
    }
}

class Board extends React.Component {
    constructor(props){
        super(props)
            this.state = {
orderbook: {
ask_levels: [{price: 10, volume: 11}, {price:12, volume:15}],
            bid_levels: [{price: 9, volume: 5}, {price:6, volume:10}]},
           trades: [{price: 10, volume: 11, side:"BUY"}, {price: 15, volume: 13, side: "SELL"}]

            }
    }

    renderOrderbook(i) {
        return <Orderbook 
            orderbook={this.state.orderbook}
        onClick={() => this.handleClick(i)} />
    }

    renderTradeInput(i) {
        return <TradeInput/>
    }

    renderOrderList(i) {
        return <OrderList orders={this.state.trades} />
    }

    renderTradeList(i) {
        return <TradeList trades={this.state.trades} />
    }

    handleClick(i)
    {
        const squares = this.state.squares.slice();
        squares[i] = 'X';
        this.setState({squares: squares});
    }

    render() {

        return (
                <div className="display">
                <div className="tradeInputAndOrderbook">
                {this.renderTradeInput()}
                {this.renderOrderbook()}
                </div>
                <div className="tradeListAndPnL">
                {this.renderTradeList()}
                {this.renderOrderList()}
                </div>
                </div>
               );
    }
}

class Game extends React.Component {
    render() {
        return (
                <div className="game">
                <div className="game-board">
                <Board />
                </div>
                </div>
               );
    }
}

// ========================================

ReactDOM.render(
        <Game />,
        document.getElementById('root')
        );
