import {
      CartesianGrid,
      Legend,
      ResponsiveContainer,
	  Scatter, 
ScatterChart,
      Line,
      LineChart,
      Tooltip,
      XAxis,
    YAxis,
} from 'recharts'

import React from 'react'

class OrderbookGraph extends React.Component {
    render() {
        console.log("Bids: ", this.props.bid)
        let data = []
        this.props.bid.map((i) => data.push({value: i.y, time: i.t}))
        console.log(data)
        return (
            <div className="orderbookGraph">
  <ResponsiveContainer width = '95%' height = {300} >
    <LineChart width={200} height={200} data={data}>
      <Line type="monotone" dataKey="value" stroke="#00ff00"/>
      <CartesianGrid stroke="#ccc"/>
      <XAxis datakey="time"/>
    </LineChart>
  </ResponsiveContainer>
            </div>
        )
    }
}
