import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Orderbook extends React.Component {
    render() {
    const ask_levels = this.props.orderbook.ask_levels.map(
        (level) => {
            return <tr>
                <td><button className="hitButton">Buy</button></td>
                <td></td><td className="bold">${level.price}</td><td>{level.volume}</td></tr>
        }
    )

    const bid_levels = this.props.orderbook.bid_levels.map(
        (level) => {
            return <tr><td>{level.volume}</td><td className="bold">${level.price}</td><td></td>
                <td><button className="hitButton">Buy</button></td></tr>
        }
    )

    return (
      <div>
      <table>
        <tr>
        <td className="bidHeader">Vol</td>
        <td className="bidHeader">Bid</td>
        <td className="askHeader">Ask</td>
        <td className="askHeader">Vol</td>
        </tr>
        {ask_levels}
        {bid_levels}
      </table>
      </div>
    );
  }
}

class Board extends React.Component {
  constructor(props){
      super(props)
      this.state = {
          squares: Array(9).fill(null)
      }
  }

  renderOrderbook(i) {
      return <Orderbook 
        orderbook={{
            ask_levels: [{price: 10, volume: 11}, {price:12, volume:15}],
            bid_levels: [{price: 9, volume: 5}, {price:6, volume:10}]}}
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)} />
  }

  handleClick(i)
  {
      const squares = this.state.squares.slice();
      squares[i] = 'X';
      this.setState({squares: squares});
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderOrderbook(0)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
