import React from 'react';
import ReactDOM from 'react-dom';

import { Route, BrowserRouter as Router } from 'react-router-dom'

import TraderInterface from './trader_interface.jsx';
import Login from './login_page.js';
import './index.css';

function uuidv4(){
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
		return v.toString(16);
	  });
}

class ExchangeInterface extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            websocket: null
        }
    }

    connect(host){
        this.setState((state, props) => {
            return {
                websocket: new WebSocket(props.exchange_host)
            }
        });
    }

    send(data){
        this.state.websocket.send(data);
    }

    send_order(side, price, volume, trader_id){
        let order = {
            type:"insert", 
            trader_id: trader_id,
            order_id: uuidv4(),
            side: side,
            price: price,
            volume: volume}
    }


}

class Root extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            websocket: null
        }
    }
}


class Main extends React.Component {
    render() {
        return (
			<div className="main">
			  <div className="main-trader-interface">
				<TraderInterface />
			  </div>
			</div>
		   );
    }
}

// ========================================

const routing = (
  <Router>
    <div>
      <Route exact path="/" component={Login} />
      <Route path="/TraderInterface" component={Main} />
    </div>
  </Router>
)

ReactDOM.render(routing, document.getElementById('root'))
