//import {Line} from 'react-chartjs-2'
import ReactChartkick, { LineChart } from 'react-chartkick'
import Chart from 'chart.js'

import React from 'react'

ReactChartkick.addAdapter(Chart)


class OrderbookGraph extends React.Component {
    render() {
        console.log("Bids: ", this.props.bid)
        let data = []
        this.props.bid.map((i) => data.push([i.t, i.y]))
        console.log(data)
        options =  {
                      responsive: true,
                      maintainAspectRatio: false,
                      title: {
                          display: true,
                          text: "Orderbook and Trades"
                      },
                      scales: {
                          yAxes: [{
                              distribution: 'linear',
                              ticks: {
                                  beginAtZero:true
                              }
                          }],
                          xAxes: [{
                              type: 'time',
                              distribution: 'linear',
                              ticks: {
                                  source: 'data'
                              }
                          }]

                      },
                      pan: {
                          enabled: true,
                          mode: 'x',
                          speed: 10,
                          threshold: 10,
                          onPan: function() {console.log("PANNED");}
                      },
                      zoom: {
                          enabled: true,
                          mode: 'x',
                          sensitivity: 0.25
                      }
                  }
        return (
            <div className="orderbookGraph">
                <LineChart 
                    data={data} 
                    dataset={{label: 'Bid', steppedLine: 'before', borderColor: "#00ff00"}}
                    library={options}
                />
            </div>
        )
    }
}
