import React from 'react';

function uuidv4(){
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
		return v.toString(16);
	  });
}

class ExchangeInterface {
    constructor(onopen){
        this.onopen = onopen;
        this.websocket = null;
    }

    render(){
        return null;
    }

    connect(host){
        console.log("Opening connection to", host)
        this.websocket = new WebSocket(host)
        this.websocket.onopen = (event) => {
            console.log("WebSocket onopen")
            this.onopen(event)
        }
        this.websocket.onclose = (event) => {
            console.log("WebSocket onopen")
            this.onopen(event)
        }
    }

    send(data){
        this.state.websocket.send(JSON.stringify(data));
    }

    send_order(side, price, volume, trader_id){
        let order = {
            type:"insert", 
            trader_id: trader_id,
            order_id: uuidv4(),
            side: side,
            price: price,
            volume: volume}
        console.log("Sending order", order);
        this.send(order);
    }

    register_trader(trader_id){
        let message = {
            type: "sync_state",
            trader_id: trader_id
        }

        console.log("Sending trader_id", trader_id);
        this.send(message);
    }

    send_cancel(order_id, trader_id){
        let cancel = {
			type:"cancel",
			trader_id: trader_id, 
            order_id: order_id}
        console.log("Sending cancel", cancel);
        this.send(cancel)
    }
}

export default ExchangeInterface
